messages.content:
package policy

default allow = true

allow {
  input.review.namespace != "regulated"
}

allow {
  input.review.namespace == "regulated"
  not input.review.container.securityContext.runAsNonRoot == false
}

violation[msg] {
  input.review.namespace == "regulated"
  input.review.container.securityContext.runAsNonRoot == false
  msg := "runAsNonRoot must be true in regulated namespaces"
}
